    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
String email = txtEmail.getText().trim();
        if (email.isEmpty()) {
            lblMsg.setText("Please enter your email address");
        } else if (!isValidEmail(email)) {
            lblMsg.setText("Your email address is Wrong");
        } else {
            lblMsg.setText("Your email address is correct");
        }
        // TODO add your handling code here:
    }//GEN-LAST:event_jButton1ActionPerformed

    
    public boolean isValidEmail(String email) {
        boolean status = false;
        int att = email.indexOf("@");
        int dtt = email.lastIndexOf(".");
        if (att > 0 && dtt < email.length() - 1 && att < dtt - 1) {
            status = true;
        }
        return status;
    }
