    private void jButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonActionPerformed
        String item= cmbValume.getSelectedItem().toString();
        double fish = Double.parseDouble(txtFish.getText());
        
        if (item.equals("KG")) {
            double cal = (fish*140); 
            jDisplay.setText(String.valueOf(cal));
            
        }
        else{
        double cal = (fish/1000*140); 
            jDisplay.setText(String.valueOf(cal));
        }
    }//GEN-LAST:event_jButtonActionPerformed

